INSERT INTO `sozler` (`id`, `soyleyen`, `soz`) VALUES
(1, 'Cemal Süreya', 'Senin çelme taktığın yerden başlıyorum hayata. Varsın yara içinde kalsın dizlerim; yüreğim kadar acımaz nasıl olsa. '),
(2, 'Can Yücel', 'Yaşamayı bu soğumuş cehennemde, ölü bir dost gibi içim titreyerek değil sade, yaşamayı yaşamak istiyorum! '),
(3, 'Michel de Montaigne', 'Kral da, dilenci de aynı iştahla acıkırlar.'),
(4, 'Küçük İskender', 'Senin için ölürüm dedi. Benim için zaten öldün dedim. Cesedini alıp çıktı...'),
(5, 'Cenap Şahabettin', 'Yalnız seni sevenleri sevmek sevgi değil, değiş tokuştur.');

